import { useContext } from "react";
import SortContext from "../context/sortContext";

function Sort() {
  const { sortOption, setSortOption } = useContext(SortContext);
  return (
    <form>
      <select
        value={ sortOption }
        onChange={ (e) => setSortOption(e.target.value) }
      >
        { ['population', 'orbital_period',
          'diameter', 'rotation_period', 'surface_water']
          .map((opt, index) => (
            <option
              key={ index }
              value={ opt }
            >
              {opt}
            </option>
          ))}
      </select>
      <label htmlFor="asc">Ascendente</label>
      <input
        type="radio"
        name="sort-radio"
        id="asc"
      />
      <label htmlFor="dsc">Descendente</label>
      <input
        type="radio"
        name="sort-radio"
        id="dsc"
      />
    </form>
  );
}

export default Sort;
