import { useContext } from 'react';
import useFetch from '../hooks/useFetch';
import FormContext from '../context/formContext';
import FilterContext from '../context/filterContext';
import SortContext from '../context/sortContext';

function Table() {
  const { DATA } = useFetch();
  const { search } = useContext(FormContext);
  const { HandleFilterCases } = useContext(FilterContext);
  const { SortBy } = useContext(SortContext);

  return (
    <div className="w-84 border-red-400 border-2">
      { DATA.length
        ? (
          <table className="table-fixed">
            <thead>
              <tr>
                { Object.keys(DATA[0]).map((objKey, index) => (
                  <th
                    key={ index }
                    className="text-yellow-300"
                  >
                    {objKey}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              { DATA.filter((planet) => planet.name.toLowerCase().includes(search))
                .filter(HandleFilterCases)
                .sort((a, b) => SortBy(a, b))
                .map((planet, index) => (
                  <tr key={ index }>
                    <td data-testid="planet-name">{planet.name}</td>
                    <td>{planet.rotation_period}</td>
                    <td>{planet.orbital_period}</td>
                    <td>{planet.diameter}</td>
                    <td>{planet.climate}</td>
                    <td>{planet.gravity}</td>
                    <td>{planet.terrain}</td>
                    <td>{planet.surface_water}</td>
                    <td>{planet.population}</td>
                    <td className="text-ellipsis">{planet.films}</td>
                    <td>{planet.created}</td>
                    <td>{planet.edited}</td>
                    <td>{planet.url}</td>
                  </tr>
                ))}
            </tbody>
          </table>
        )
        : (
          <p>LOADING...</p>
        )}
    </div>
  );
}

export default Table;
