import { useContext } from 'react';
import useFetch from '../hooks/useFetch';
import FormContext from '../context/formContext';
import FilterContext from '../context/filterContext';
import SortContext from '../context/sortContext';

function Table() {
  const { DATA } = useFetch();
  const { search } = useContext(FormContext);
  const { HandleFilterCases } = useContext(FilterContext);
  const { SortBy } = useContext(SortContext);

  return (
    <div className="border-red-400 border-2 w-screen">
      { DATA.length
        ? (
          <div className="border-blue-500 border-4 overflow-x-auto">
            <table
              className="w border-separate border-spacing-2 border border-slate-500"
            >
              <thead>
                <tr>
                  { Object.keys(DATA[0]).map((objKey, index) => (
                    <th
                      key={ index }
                      className="text-yellow-300 border border-slate-600 w-8"
                    >
                      {objKey}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody className="w-72">
                { DATA.filter((planet) => planet.name.toLowerCase().includes(search))
                  .filter(HandleFilterCases)
                  .sort((a, b) => SortBy(a, b))
                  .map((planet, index) => (
                    <tr key={ index }>
                      <td
                        className="border border-slate-700"
                        data-testid="planet-name"
                      >
                        {planet.name}
                      </td>
                      <td
                        className="border border-slate-700"
                      >
                        {planet.rotation_period}
                      </td>
                      <td className="border border-slate-700">{planet.orbital_period}</td>
                      <td className="border border-slate-700">{planet.diameter}</td>
                      <td className="border border-slate-700">{planet.climate}</td>
                      <td className="border border-slate-700">{planet.gravity}</td>
                      <td className="border border-slate-700">{planet.terrain}</td>
                      <td className="border border-slate-700">{planet.surface_water}</td>
                      <td className="border border-slate-700">{planet.population}</td>
                      <td className="text-clip w-24">{planet.films}</td>
                      <td className="border border-slate-700">{planet.created}</td>
                      <td className="border border-slate-700">{planet.edited}</td>
                      <td className="border border-slate-700">{planet.url}</td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        )
        : (
          <p>LOADING...</p>
        )}
    </div>
  );
}

export default Table;
