import { useContext } from 'react';
import useFetch from '../hooks/useFetch';
import FormContext from '../context/formContext';
import FilterContext from '../context/filterContext';
import SortContext from '../context/sortContext';

function Table() {
  const { DATA } = useFetch();
  const { search } = useContext(FormContext);
  const { HandleFilterCases } = useContext(FilterContext);
  const { SortBy } = useContext(SortContext);

  return (
    <div className="overflow-x-auto">
      <table className="min-w-full">
        <thead>
          <tr>
            {Object.keys(DATA[0]).map((objKey, index) => (
              <th
                key={ index }
                className="px-6 py-3 bg-gray-50
                text-left text-xs leading-4
                font-medium text-gray-500 uppercase tracking-wider"
              >
                {objKey}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {DATA.filter((planet) => planet.name.toLowerCase().includes(search))
            .filter(HandleFilterCases)
            .sort((a, b) => SortBy(a, b))
            .map((planet, index) => (
              <tr key={ index }>
                <td className="px-6 py-4 whitespace-no-wrap border-b border-gray-200">
                  {planet.name}
                </td>
                <td className="px-6 py-4 whitespace-no-wrap border-b border-gray-200">
                  {planet.rotation_period}
                </td>
                <td className="px-6 py-4 whitespace-no-wrap border-b border-gray-200">
                  {planet.orbital_period}
                </td>
                <td className="px-6 py-4 whitespace-no-wrap border-b border-gray-200">
                  {planet.diameter}
                </td>
                <td className="px-6 py-4 whitespace-no-wrap border-b border-gray-200">
                  {planet.climate}
                </td>
                <td className="px-6 py-4 whitespace-no-wrap border-b border-gray-200">
                  {planet.gravity}
                </td>
                <td className="px-6 py-4 whitespace-no-wrap border-b border-gray-200">
                  {planet.terrain}
                </td>
                <td className="px-6 py-4 whitespace-no-wrap border-b border-gray-200">
                  {planet.surface_water}
                </td>
                <td className="px-6 py-4 whitespace-no-wrap border-b border-gray-200">
                  {planet.population}
                </td>
                <td className="px-6 py-4 whitespace-no-wrap border-b border-gray-200">
                  <div className="w-64 truncate">{planet.films}</div>
                </td>
                <td className="px-6 py-4 whitespace-no-wrap border-b border-gray-200">
                  {planet.created}
                </td>
                <td className="px-6 py-4 whitespace-no-wrap border-b border-gray-200">
                  {planet.edited}
                </td>
                <td className="px-6 py-4 whitespace-no-wrap border-b border-gray-200">
                  {planet.url}
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
