function Sort() {
  return (
    <form>
      <select>
        { ['population', 'orbital_period',
          'diameter', 'rotation_period', 'surface_water']
          .map((opt, index) => (
            <option key={ index }>{opt}</option>
          ))}
      </select>
      <label htmlFor="asc">Ascendente</label>
      <input
        type="radio"
        name="sort-radio"
        id="asc"
      />
      <label htmlFor="dsc">Descendente</label>
      <input
        type="radio"
        name="sort-radio"
        id="dsc"
      />
    </form>
  );
}

export default Sort;
