// import { useState } from 'react';
import PropTypes from 'prop-types';
import { useContext } from 'react';
import FilterContext from './filterContext';
import useInput from '../hooks/useInput';
import PlanetsContext from './planetsContext';
import FormContext from './formContext';

function FilterProvider({ children }) {
  const columnFilter = useInput('population');
  const comparisonFilter = useInput('maior que');
  const valueFilter = useInput('');

  const { planets } = useContext(PlanetsContext);
  const { search } = useContext(FormContext);

  const handleFilter = () => {
    const column = columnFilter.value;
    const comparison = comparisonFilter.value;
    const valueInput = valueFilter.value;

    const planetsFilterByName = planets
      .filter((planet) => planet.name.toLowerCase().includes(search));

    const planetFilterByNameNValue = planetsFilterByName.filter((planet) => {
      if (valueInput === 0) {
        return true;
      }
      switch (comparison) {
      case 'maior que':
        return (planet[column] > valueInput);
      case 'menor que':
        return (planet[column] < valueInput);
      case 'igual a':
        return (planet[column] === valueInput);
      default:
        return planet;
      }
    });

    return {
      planetFilterByNameNValue,
    };
  };

  return (
    <FilterContext.Provider
      value={
        { columnFilter, comparisonFilter, valueFilter, handleFilter }
      }
    >
      { children }
    </FilterContext.Provider>
  );
}

FilterProvider.propTypes = {
  children: PropTypes.element.isRequired,
};

export default FilterProvider;
