import { useContext } from 'react';
import FilterContext from '../context/filterContext';

function ActiveFilter() {
  const { activeFilters } = useContext(FilterContext);

  return (
    <div>
      { activeFilters.length > 0
        && (
          <div
            className="flex flex-col items-center"
          >
            <h3
              className="text-yellow-300 font-bold
            decoration-double decoration-black
            decoration-2
            underline mb-2
            "
            >
              Filtros ativos:
            </h3>
            <div className="flex flex-row gap-2">
              { activeFilters.map((filter, index) => (
                <div
                  key={ index }
                  className="flex flex-row gap-2 bg-zinc-950 rounded-md w-52 h-6
                  justify-center"
                >
                  <p data-testid="filter" className="text-stone-400 text-sm">
                    { `${filter.column} ${filter.comparison} = ${filter.value}` }
                  </p>
                  <button
                    className="bg-yellow-300 rounded-full w-4 h-4 text-stone-400"
                    type="button"
                    onClick={ () => RemoveFilters(filter.column) }
                  >
                    X
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}
    </div>
  );
}

export default ActiveFilter;
