import PropTypes from 'prop-types';
import { useState } from 'react';
import SortContext from './sortContext';

function SortProvider({ children }) {
  const [sort, setSort] = useState({
    order: {
      column: '',
      sort: '',
    },
  });

  const [sortOption, setSortOption] = useState('population');
  const [radio, setRadio] = useState('');

  function SortByOption(planetA, planetB) {
    if (!sort.order.column || !sort.order.sort) {
      return true;
    }
    switch (sort.order.sort) {
    case 'ASC':
      return Number(planetA[sort.order.column]) - Number(planetB[sort.order.column]);
    case 'DESC':
      return Number(planetB[sort.order.column]) - Number(planetA[sort.order.column]);
    default:
      break;
    }
  }

  return (
    <SortContext.Provider
      value={
        { sort,
          sortOption,
          setSortOption,
          radio,
          setRadio,
          setSort,
          SortByOption }
      }
    >
      {children}
    </SortContext.Provider>
  );
}

SortProvider.propTypes = {
  children: PropTypes.element.isRequired,
};

export default SortProvider;
