import { useContext } from 'react';
import FilterContext from '../context/filterContext';

function Filter() {
  const { columnFilter, comparisonFilter, valueFilter,
    handleFilter } = useContext(FilterContext);

  return (
    <form>
      <select
        data-testid="column-filter"
        value={ columnFilter.value }
        onChange={ columnFilter.onChange }
      >
        <option value="population">population</option>
        <option value="orbital_period">orbital_period</option>
        <option value="diameter">diameter</option>
        <option value="rotation_period">rotation_period</option>
        <option value="surface_water">surface_water</option>
      </select>
      <select
        data-testid="comparison-filter"
        value={ comparisonFilter.value }
        onChange={ comparisonFilter.onChange }
      >
        <option value="maior que">maior que</option>
        <option value="menor que">menor que</option>
        <option value="igual a">igual a</option>
      </select>
      <input
        type="number"
        data-testid="value-filter"
        value={ valueFilter.value }
        onChange={ valueFilter.onChange }
      />
      <button
        type="button"
        data-testid="button-filter"
        onClick={ handleFilter }
      >
        Filtrar
      </button>
    </form>
  );
}

export default Filter;
